import React from "react";
import FormInput from "../../component/form-input/form-input.component";
import CustomButton from "../../component/custom-button/custom-button.component";
import { auth, createUserProfileDocument } from "../../firebase/firebase.utils";

class ContractorMaster extends React.Component {
	constructor() {
		super();

		this.state = {
			displayName: "",
			email: "",
			password: "",
			confirmPassword: "",
			companyName: "",
			supervisorName: "",
			supervisorContact: "",
			bankName: "",
			accountNo: "",
			IFSCCode: "",
			Dateofpayment: "",
			Amount: "",
			refNo: "",
			forNoofEmployee: "",
			HCType: "",
		};
	}

	handleSubmit = async (event) => {
		event.preventDefault();

		const contractorDetails = {
			displayName,
			email,
			password,
			confirmPassword,
			companyName,
			supervisorName,
			supervisorContact,
			bankName,
			accountNo,
			IFSCCode,
			Dateofpayment,
			Amount,
			refNo,
			forNoofEmployee,
			HCType,
		};

		if (password !== confirmPassword) {
			alert("password dosn't match");
			return;
		}

		try {
			const { user } = await auth.createUserWithEmailAndPassword(
				email,
				password,
			);

			await createUserProfileDocument(user, { displayName });

			this.setState({
				displayName: "",
				email: "",
				password: "",
				confirmPassword: "",
				companyName: "",
				supervisorName: "",
				supervisorContact: "",
				bankName: "",
				accountNo: "",
				IFSCCode: "",
				Dateofpayment: "",
				Amount: "",
				refNo: "",
				forNoofEmployee: "",
				HCType: "",
			});
		} catch (error) {
			console.error(error);
		}
	};

	handleChange = (event) => {
		const { name, value } = event.target;

		this.setState({ [name]: value });
	};

	render() {
		const {
			displayName,
			email,
			password,
			confirmPassword,
			companyName,
			supervisorName,
			supervisorContact,
			bankName,
			accountNo,
			IFSCCode,
			Dateofpayment,
			Amount,
			refNo,
			forNoofEmployee,
			HCType,
		} = this.state;

		return (
			<div className="form-main-container">
				<h2 className="title">Company Registration form</h2>
				<span>Register your company for login credential.</span>

				<form
					className="contractor-registration-form"
					onSubmit={this.handleSubmit}
				>
					<FormInput
						type="text"
						name="displayName"
						value={displayName}
						onChange={this.handleChange}
						label="Display Name"
						required
					/>

					<FormInput
						type="email"
						name="email"
						value={email}
						onChange={this.handleChange}
						label="Email"
						required
					/>

					<FormInput
						type="password"
						name="password"
						value={password}
						onChange={this.handleChange}
						label="Password"
						required
					/>

					<FormInput
						type="password"
						name="confirmPassword"
						value={confirmPassword}
						onChange={this.handleChange}
						label="Confirm Password"
						required
					/>

					<FormInput
						type="text"
						name="companyName"
						value={companyName}
						onChange={this.handleChange}
						label="Company Name"
						required
					/>

					<FormInput
						type="text"
						name="supervisorName"
						value={supervisorName}
						onChange={this.handleChange}
						label="Supervisor Name"
						required
					/>
					<FormInput
						type="text"
						name="supervisorContact"
						value={supervisorContact}
						onChange={this.handleChange}
						label="Supervisor Contact No."
						required
					/>
					<FormInput
						type="text"
						name="bankName"
						value={bankName}
						onChange={this.handleChange}
						label="Bank Name"
						required
					/>
					<FormInput
						type="text"
						name="accountNo"
						value={accountNo}
						onChange={this.handleChange}
						label="Account No"
						required
					/>
					<FormInput
						type="text"
						name="IFSCCode"
						value={IFSCCode}
						onChange={this.handleChange}
						label="IFSC Code"
						required
					/>
					<FormInput
						type="text"
						name="Dateofpayment"
						value={Dateofpayment}
						onChange={this.handleChange}
						label="Date of Payment"
						required
					/>
					<FormInput
						type="text"
						name="Amount"
						value={Amount}
						onChange={this.handleChange}
						label="Ammount"
						required
					/>
					<FormInput
						type="text"
						name="refNo"
						value={refNo}
						onChange={this.handleChange}
						label="Refrence No."
						required
					/>
					<FormInput
						type="text"
						name="forNoofEmployee"
						value={forNoofEmployee}
						onChange={this.handleChange}
						label="For No. of Employee"
						required
					/>
					<FormInput
						type="text"
						name="HCType"
						value={HCType}
						onChange={this.handleChange}
						label="Health Checkup Type"
						required
					/>

					<CustomButton type="submit">SUBMIT</CustomButton>
				</form>
			</div>
		);
	}
}
export default ContractorMaster;
