import React from "react";
import "./form-dropdown.styles.scss";

const FormDropdown = ({ handleChange, label, options, ...otherProps }) => (
	<div className="group">
		<input className="form-dropdown" onChange={handleChange} {...otherProps}>
			<strong className="title">{options.value}</strong>
		</input>
		{label ? (
			<label
				className={`${
					otherProps.value.length ? "shrink" : ""
				} form-input-label`}
			>
				{label}
			</label>
		) : null}
	</div>
);

export default FormDropdown;
