import React from "react";
import "./form-dropdown.styles.scss";
import Select, { components } from "react-select";

const FormDropdown = ({ handleChange, label, options, ...otherProps }) => (
	<div className="group">
		<Select className="form-dropdown" onChange={handleChange} {...otherProps}>
			components=
			{{
				Option,
			}}
		</Select>
		{label ? (
			<label
				className={`${
					otherProps.value.length ? "shrink" : ""
				} form-input-label`}
			>
				{label}
			</label>
		) : null}
	</div>
);

export default FormDropdown;

