import React, { Component } from "react";

export default class HeaderDateTime extends Component {
	componentDidMount() {
		setInterval(
			() =>
				this.setState({
					curTime: new Date().toLocaleString("en-GB", { timeZone: "UTC" }),
				}),
			1000,
		);
	}

	state = { curTime: new Date().toLocaleString("en-GB", { timeZone: "UTC" }) };

	render() {
		return <div>{this.state.curTime}</div>;
	}
}
