import React from "react";
import { Link } from "react-router-dom";
import { connect } from "react-redux";
import { createStructuredSelector } from "reselect";
import { ReactComponent as Logo } from "../../assets/HosCross.svg";
import CartIcon from "../card-icon/card-icon.component";
import CartDropdown from "../cart-dropdown/cart-dropdown.component";

import { selectCartHidden } from "../../redux/cart/cart.selectors";
import { selectCurrentUser } from "../../redux/user/user.selectors";

import { auth } from "../../firebase/firebase.utils";
import "./header.styles.scss";

var element;
function tick() {
	element = <h4> {new Date().toLocaleTimeString()}.</h4>;
}
setInterval(tick, 1000);
const Header = ({ currentUser, hidden }) => (
	<>
		<div className="header1">
			<h4>VIVEKANANDA KENDRA BORL HOSPITAL</h4>
			{element}....
		</div>

		<div className="header">
			<Link className="logo-container" to="/">
				<Logo className="logo" />
			</Link>
			<div className="options">
				<Link className="option" to="/shop">
					SHOP
				</Link>
				<Link className="option" to="/contactus">
					CONTACT US
				</Link>
				<Link className="option" to="/sme">
					SME
				</Link>
				<Link className="option" to="/Example">
					Example
				</Link>

				{currentUser ? (
					<div className="option" onClick={() => auth.signOut()}>
						SIGN OUT<span>( {auth.currentUser.email} )</span>
					</div>
				) : (
					<Link className="option" to="/signin">
						SIGN IN
					</Link>
				)}
				<CartIcon />
			</div>
			{hidden ? null : <CartDropdown />}
		</div>
	</>
);
const mapStateToProps = createStructuredSelector({
	currentUser: selectCurrentUser,
	hidden: selectCartHidden,
});

export default connect(mapStateToProps)(Header);
