import React from "react";
import "./form-dropdown.styles.scss";
import Select, { components } from "react-select";

const Option = (props) => {
	return (
		<components.Option {...props}>
			<div className="left">{props.isSelected ? "✔" : ""}</div>
			<div className="right">
				<strong className="title">{props.data.label}</strong>
				<div>{props.data.description}</div>
			</div>
		</components.Option>
	);
};

const FormDropdown = ({ handleChange, label, options, ...otherProps }) => (
	<div className="group">
		<Select className="form-dropdown" onChange={handleChange} {...otherProps}>
			{{
				options,
			}}
		</Select>
	</div>
);

export default FormDropdown;

